import React, { useEffect, useState } from "react";
import { Switch, Route, useParams, useRouteMatch } from "react-router-dom";
import DeckList from "./DeckList";
import Study from "./Study";
import DeckForm from "./DeckForm";
import { readDeck, listCards, deleteCard } from "../utils/api"

function Deck() {
  const { deckId } = useParams();
  const { url } = useRouteMatch();

  const [deck, setDeck] = useState({});
  const [cards, setCards] = useState([]);

  useEffect(() => {
    async function getDeck() {
      try {
        const response = await readDeck(deckId);
        setDeck(response);
      } catch (error) {
          console.log(error);
      }
    }
    getDeck();
  }, [deckId]);

  useEffect(() => {
      async function getCards() {
        try {
          const response = await listCards(deckId);
          setCards(response);
        } catch (error) {
            console.log(error);
        }
      }
      getCards();
    }, [deckId]);

    const handleDeleteCard = (id) => {
      if (window.confirm("Are you sure you want to delete this card?")) {
        deleteCard(id);
        window.location.reload(true);
      }
    }

  return (
    <Switch>
      <Route exact={true} path={`${url}`}>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item"><a href="/"><span className="oi oi-home"></span> Home</a></li>
            <li className="breadcrumb-item"><a href={`/decks/${deckId}`}>{deck.name}</a></li>
          </ol>
        </nav>
        <div className="card mb-4">
            <div className="card-body">
                <div className="d-flex justify-content-between">
                    <h2>{deck.name}</h2>
                </div>
                <p>{deck.description}</p>
                <a href={`${url}/study`} className="btn btn-primary mr-1"><span className="oi oi-book"></span> Study <span className="badge badge-light">{cards.length}</span></a>
                <a href={`${url}/edit`} className="btn btn-secondary mr-1"><span className="oi oi-cog"></span> Edit</a>
                <a href={`${url}/cards/new`} className="btn btn-secondary"><span className="oi oi-plus"></span> Add Cards</a>
                <a href={`/`} className="btn btn-danger float-right"><span className="oi oi-trash"></span></a>
            </div>
        </div>
        <h3>Cards</h3>
        {cards.map((card) => (
        <div className="card mb-2">
            <div className="card-body d-flex">
              <div className="col-6">
                <div className="alert alert-primary">
                  {card.front}
                </div>
              </div>
              <div className="col-6">
                <div className="alert alert-success">
                  {card.back}
                </div>
                  <button type="delete" className="btn btn-danger float-right" onClick={() => handleDeleteCard(card.id)}><span className="oi oi-trash"></span></button>
                  <a href={`${url}/cards/${card.id}/edit`} className="btn btn-secondary mr-1 float-right">Edit</a>
              </div>
            </div>
        </div>
        ))}
      </Route>
      <Route path={`/decks/:deckId/study`}>
        <Study name={deck.name} />
      </Route>
      <Route path={`/decks/:deckId/edit`}>
        <DeckForm />
      </Route>
      <Route path={`/decks/:deckId/cards/new`}>
        <p>Create New Card</p>
      </Route>
      <Route path={`/decks/:deckId/cards/:cardId/edit`}>
        <p>Edit Card</p>
      </Route>
    </Switch>
  );
}

export default Deck;
