import React, { useEffect, useState } from "react";
import { Switch, Route, useParams, useRouteMatch } from "react-router-dom";

function Deck() {
  const { deckId } = useParams();
  const { url } = useRouteMatch();

  console.log(url);

  const [deck, setDeck] = useState({});

  useEffect(() => {
    const abortController = new AbortController();
    async function getDeck() {
      try {
        const response = await fetch(`${process.env.REACT_APP_DECKS_URL}/${deckId}`, abortController.signal);
        const data = await response.json();
        setDeck(data);
      } catch (error) {
          console.log(error);
      }
    }
    getDeck();

    return () => abortController.abort();
  }, [deckId])

  return (
    <Switch>
      <Route path={url}>
        <div className="card mb-2">
            <div className="card-body">
                <div className="d-flex justify-content-between">
                    <h2>{deck.name}</h2>
                    <span className="text-secondary"># Cards</span>
                </div>
                <p>{deck.description}</p>
                <a href={`${url}/study`} className="btn btn-primary mr-1"><span className="oi oi-book"></span> Study</a>
                <a href={`${url}/edit`} className="btn btn-secondary mr-1"><span className="oi oi-cog"></span> Edit</a>
                <a href={`${url}/cards/new`} className="btn btn-secondary"><span className="oi oi-plus"></span> Add Cards</a>
                <a href={`/`} className="btn btn-danger float-right"><span className="oi oi-trash"></span></a>
            </div>
        </div>
      </Route>
      <Route path={`/decks/${deckId}/study`}>
        <p>Study Deck</p>
      </Route>
      <Route path={`${url}/edit`}>
        <p>Edit Deck</p>
      </Route>
      <Route path={`${url}/cards/new`}>
        <p>Create New Card</p>
      </Route>
      <Route path={`${url}/cards/:cardId/edit`}>
        <p>Edit Card</p>
      </Route>
    </Switch>
  );
}

export default Deck;
