import React, { useEffect, useState } from "react";
import { Switch, Route } from "react-router-dom";
import Header from "./Header";
import DeckList from "../Components/DeckList";
import Deck from "../Components/Deck";
import CreateDeck from "../Components/CreateDeck";
import NotFound from "./NotFound";
import { listDecks, listCards, deleteDeck } from "../utils/api";

function Layout() {
  const [decks, setDecks] = useState([]);

  useEffect(() => {
    const abortController = new AbortController();
    const signal = abortController.signal;

    async function getDecks() {
      try {
        const response = await listDecks(signal);
        setDecks(response);
      } catch (error) {
        console.log(error);
      }
    }
    getDecks();

    return () => abortController.abort();
    }, []);

    const handleDeleteDeck = (id) => {
      if (window.confirm("Are you sure you want to delete this deck?")) {
        deleteDeck(id);
        window.location.reload(true);
      } 
    }

    const setNumberOfCards = () => {

    }

  return (
    <>
      <Header />
      <main className="container">
        <Switch>
          <Route exact={true} path={`/`}>
            <a href={`/decks/new`} className="btn btn-secondary mb-2"><span className="oi oi-plus"></span> Create Deck</a>
            <div className="list-group">
                {decks.map((deck) => (
                  <div key={deck.id} className="list-group-item list-group-item-action flex-column align-items-start">
                    <div className="d-flex w-100 justify-content-between">
                        <h2 className="mb-1">{deck.name}</h2>
                    </div>
                    <p className="mb-1">{deck.description}</p>
                    <a href={`/decks/${deck.id}`} className="btn btn-secondary mr-1"><span className="oi oi-eye"></span> View</a>
                    <a href={`/decks/${deck.id}/study`} className="btn btn-primary"><span className="oi oi-book"></span> Study <span className="badge badge-light">#</span></a>
                    <button type="delete" className="btn btn-danger float-right" onClick={() => handleDeleteDeck(deck.id)}><span className="oi oi-trash"></span></button>
                  </div>
                ))}
            </div>
          </Route>
          <Route path={"/decks/new"}>
            <CreateDeck />
          </Route>
          <Route path={"/decks/:deckId"}>
            <Deck count={setNumberOfCards} />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </main>
    </>
  );
}

export default Layout;
