import React, { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom";
import { listCards } from "../utils/api"

function Study({ name }) {

  const initialCardData = {
    validDeck: false
  }

  const { deckId } = useParams();
  const history = useHistory();

  const [cards, setCards] = useState([]);
  const [cardData, setCardData] = useState({});

  useEffect(() => {
    async function getCards() {
      try {
        const response = await listCards(deckId);
        setCards(response);
        if (response.length > 3) {
          setCardData({
            validDeck: true,
            isFlipped: false,
            front: response[0].front,
            back: response[0].back,
            progress: 0
          });
        }
      } catch (error) {
          console.log(error);
      }
    }
    getCards();

  }, [deckId]);

  const handleNextCard = () => {
    if (cards.length === cardData.progress) {
      if (window.confirm("Do you want to restart this deck?")) {
        setCardData({
          ...cardData,
          isFlipped: false,
          front: cards[0].front,
          back: cards[0].back,
          progress: 0
        });
      } else {
        history.push("/");
      }
    } else {
      const nextCard = cards.find((ignore, index) => index === cardData.progress)
      setCardData({
        isFlipped: false,
        front: nextCard.front,
        back: nextCard.back,
        progress: cardData.progress + 1
      });
    }
  }

  const flipCurrentCard = () => {
    setCardData({
      ...cardData,
      isFlipped: !cardData.isFlipped
    });
  }

  return (
    <>
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
            <li className="breadcrumb-item"><a href="/"><span className="oi oi-home"></span> Home</a></li>
            <li className="breadcrumb-item"><a href={`/decks/${deckId}`}>{name}</a></li>
            <li className="breadcrumb-item text-secondary">Study</li>
        </ol>
      </nav>
      <h2>Study: {name}</h2>
      {!cardData.validDeck ? <h3>Not enough cards...</h3> : (
      <div className="card mb-2">
        <span className="p-3 mb-2 bg-dark text-white font-weight-bold">Card {cardData.progress + 1} of {cards.length + 1}</span>
        {!cardData.isFlipped ? (
          <div className="card-body">
            <p>{cardData.front}</p>
            <button className="btn btn-secondary mr-1" onClick={flipCurrentCard}>Flip</button>
          </div>
        ) : (
          <div className="card-body">
            <p>{cardData.back}</p>
            <button className="btn btn-secondary mr-1" onClick={flipCurrentCard}>Flip</button>
            <button className="btn btn-primary" onClick={handleNextCard}>Next</button>
          </div>
        )}
      </div>
      )}
    </>
  );
}

export default Study;
