import React, { useEffect, useState } from "react";
import { Switch, Route, useParams, useRouteMatch } from "react-router-dom";

function Deck() {
  const { deckId } = useParams();
  const { url } = useRouteMatch();

  const [deck, setDeck] = useState({});

  useEffect(() => {
    const abortController = new AbortController();
    async function getDeck() {
      try {
        const response = await fetch(`${process.env.REACT_APP_DECKS_URL}/${deckId}`, abortController.signal);
        const data = await response.json();
        setDeck(data);
      } catch (error) {
          console.log(error);
      }
    }
    getDeck();

    return () => abortController.abort();
  }, [deckId])

  return (
    <div className="container">
      <Switch>
        <Route path={url}>
          <div className="card mb-2">
              <div className="card-body">
                  <div className="d-flex justify-content-between">
                      <h2>{deck.name}</h2>
                      <span className="text-secondary"># Cards</span>
                  </div>
                  <p>{deck.description}</p>
                  <a href={`/decks/${deck.id}`} className="btn btn-secondary mr-1"><span class="oi oi-eye"></span> View</a>
                  <a href={`/decks/${deck.id}/study`} className="btn btn-primary"><span class="oi oi-book"></span> Study</a>
                  <a href={`/`} className="btn btn-danger float-right"><span class="oi oi-trash"></span></a>
              </div>
          </div>
        </Route>
        <Route path={`${url}/study`}>
          <p>Study Deck</p>
        </Route>
        <Route path={`${url}/edit`}>
          <p>Edit Deck</p>
        </Route>
        <Route path={`${url}/cards/new`}>
          <p>Create New Card</p>
        </Route>
        <Route path={`${url}/cards/:cardId/edit`}>
          <p>Edit Card</p>
        </Route>
      </Switch>
    </div>
  );
}

export default Deck;
