import React from "react";
import { useHistory } from "react-router-dom";

function Form({ data, event }) {
    const history = useHistory();

    const handleSubmit = () => {
        const formData = new FormData(document.querySelector("form"));
        event(formData);
    }

    return (
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <label htmlFor="deckName">Name</label>
                <textarea className="form-control" id="deckName" name="name" defaultValue={data.name} />
            </div>
            <div className="form-group">
                <label htmlFor="deckDescription">Description</label>
                <textarea className="form-control" id="deckDescription" name="description" defaultValue={data.description} />
            </div>
            <button className="btn btn-secondary mr-1" onClick={() => history.goBack()}>Cancel</button>
            <button type="submit" className="btn btn-primary">Submit</button>
        </form>
    );
}

export default Form;
