import React from "react";
import { useHistory } from "react-router-dom";

function Form({ data, event }) {
    const history = useHistory();
    const fieldOne = data.name || data.front;
    const fieldTwo = data.description || data.back;

    console.log(fieldOne);
    console.log(fieldTwo);

    const handleSubmit = () => {
        const formData = new FormData(document.querySelector("form"));
        event(formData);
    }

    return (
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <label htmlFor="fieldOne">Name</label>
                <textarea className="form-control" id="fieldOne" name={fieldOne} defaultValue={data.name} />
            </div>
            <div className="form-group">
                <label htmlFor="fieldTwo">Description</label>
                <textarea className="form-control" id="fieldTwo" name={fieldTwo} defaultValue={data.description} />
            </div>
            <button className="btn btn-secondary mr-1" onClick={() => history.goBack()}>Cancel</button>
            <button type="submit" className="btn btn-primary">Submit</button>
        </form>
    );
}

export default Form;
