import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { listCards, readCard } from "../utils/api"

function Study({ name }) {
  const { deckId } = useParams();

  const [cards, setCards] = useState([]);

  useEffect(() => {
    async function getCards() {
      try {
        const response = await listCards(deckId);
        setCards(response);
      } catch (error) {
          console.log(error);
      }
    }
    getCards();

  }, [deckId]);

  const [card, setCard] = useState({});

  const getFirstCard = () => {
    setCard(cards[0]);
  }

  // const handleNextCard = () => {
  //   const next = cards.find((ignore, index) => index === card.id)
  // }

  const flipCurrentCard = () => {

  }

  getFirstCard();

  return (
    <>
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
            <li className="breadcrumb-item"><a href="/"><span className="oi oi-home"></span> Home</a></li>
            <li className="breadcrumb-item"><a href={`/decks/${deckId}`}>{name}</a></li>
            <li className="breadcrumb-item text-secondary">Study</li>
        </ol>
      </nav>
      <h2>Study: {name}</h2>
      <div className="card mb-2">
        <div className="card-body">
          <p>{card.front}</p>
          <button className="btn btn-secondary mr-1" onClick={flipCurrentCard}>Flip</button>
        </div>
      </div>
    </>
  );
}

export default Study;
