import React, { useState, useEffect } from "react";
import Header from "./Header";
import Deck from "../Components/Deck";
import NotFound from "./NotFound";

function Layout() {
  const [decks, setDecks] = useState([]);

  console.log(decks);

  useEffect(() => {
    const abortController = new AbortController();
    async function getDecks() {
      try {
        const response = await fetch(`${process.env.REACT_APP_DECKS_URL}`, abortController.signal);
        const data = await response.json();
        console.log(data);
        setDecks(data);
      } catch (error) {
        console.log(error);
      }
    }
    getDecks();

    return () => abortController.abort();
  }, []);

  return (
    <>
      <Header />
      <div className="container">
        {decks ? (
          decks.map((deck) => (
            <div className="card">
              <div className="card-body">
                  <h2>{deck.name}</h2>
                  <p>{deck.description}</p>
                  <a href={`/decks/${deck.id}`} className="btn btn-secondary mr-1">View</a>
                  <a href={`/decks/${deck.id}/study`} className="btn btn-primary">Study</a>
              </div>
            </div>
          ))
        ) : <NotFound />}
      </div>
    </>
  );
}

export default Layout;
