import React, { useEffect, useState } from "react";
import { Switch, Route, useParams, useRouteMatch } from "react-router-dom";
import { readDeck } from "../utils/api"

function Deck() {
  const { deckId } = useParams();
  const { url } = useRouteMatch();

  const [deck, setDeck] = useState({});

  useEffect(() => {
    async function getDeck() {
      try {
        const response = await readDeck(deckId);
        setDeck(response);
      } catch (error) {
          console.log(error);
      }
    }
    getDeck();

  }, [deckId])

  return (
    <Switch>
      <Route exact={true} path={`${url}`}>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item"><a href="/"><span className="oi oi-home"></span></a></li>
            <li className="breadcrumb-item"><a href={`/decks/${deckId}`}>{deck.name}</a></li>
          </ol>
        </nav>
        <div className="card mb-2">
            <div className="card-body">
                <div className="d-flex justify-content-between">
                    <h2>{deck.name}</h2>
                    <span className="text-secondary"># Cards</span>
                </div>
                <p>{deck.description}</p>
                <a href={`${url}/study`} className="btn btn-primary mr-1"><span className="oi oi-book"></span> Study</a>
                <a href={`${url}/edit`} className="btn btn-secondary mr-1"><span className="oi oi-cog"></span> Edit</a>
                <a href={`${url}/cards/new`} className="btn btn-secondary"><span className="oi oi-plus"></span> Add Cards</a>
                <a href={`/`} className="btn btn-danger float-right"><span className="oi oi-trash"></span></a>
            </div>
        </div>
      </Route>
      <Route path={`${url}/study`}>
        <p>Study Deck</p>
      </Route>
      <Route path={`${url}/edit`}>
        <p>Edit Deck</p>
      </Route>
      <Route path={`${url}/cards/new`}>
        <p>Create New Card</p>
      </Route>
      <Route path={`${url}/cards/:cardId/edit`}>
        <p>Edit Card</p>
      </Route>
    </Switch>
  );
}

export default Deck;
