import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import { readDeck } from "../utils/api"

function Study({ deck = {} }) {

  const { deckId } = useParams();
  const history = useHistory();

  const initialCardData = {
    validDeck: true,
    isFlipped: false,
    front: deck.cards[0].front,
    back: deck.cards[0].back,
    progress: 0
  };

  const [cardData, setCardData] = useState({});

  useEffect(() => {
    async function getDeck() {
      try {
        const response = await readDeck(deckId);
        if (response.length > 2) {
          setDeck({
            validDeck: true,
            isFlipped: false,
            front: deck.cards[0].front,
            back: deck.cards[0].back,
            progress: 0
          });
        } else {
          setDeck({
            validDeck: false
          });
        }
      } catch (error) {
          console.log(error);
      }
    }
    getDeck();
  }, [deckId, deck.cards]);

  const handleNextCard = () => {
    if (deck.cards.length === cardData.progress) {
      if (window.confirm("Do you want to restart this deck?")) {
        setCardData({
          validDeck: true,
          isFlipped: false,
          front: deck.cards[0].front,
          back: deck.cards[0].back,
          progress: 0
        });
      } else {
        history.push("/");
      }
    } else {
      const nextCard = deck.cards.find((ignore, index) => index === cardData.progress)
      setCardData({
        validDeck: true,
        isFlipped: false,
        front: nextCard.front,
        back: nextCard.back,
        progress: cardData.progress + 1
      });
    }
  }

  const flipCurrentCard = () => {
    setCardData({
      ...cardData,
      isFlipped: !cardData.isFlipped
    });
  }

  console.log(deck);

  return (
    <>
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
            <li className="breadcrumb-item"><a href="/"><span className="oi oi-home"></span> Home</a></li>
            <li className="breadcrumb-item"><a href={`/decks/${deckId}`}>{deck.name}</a></li>
            <li className="breadcrumb-item text-secondary">Study</li>
        </ol>
      </nav>
      <h2>Study: {deck.name}</h2>
      {!cardData.validDeck ? (
        <>
          <div className="alert alert-danger" role="alert">
            <h3 className="alert-heading">Not enough cards...</h3>
            There needs to be at least 3 cards in a deck in order to study. There are {deck.cards.length} cards in this deck.
          </div>
          <button className="btn btn-primary"><span className="oi oi-plus"></span> Add Cards</button>
        </>
      ) : (
        <div className="card mb-2">
          <span className="p-3 mb-2 bg-dark text-white font-weight-bold">Card {cardData.progress + 1} of {deck.cards.length + 1}</span>
          {!cardData.isFlipped ? (
            <div className="card-body text-center">
              <h4 className="mt-4 mb-5 display-5">{cardData.front}</h4>
              <button className="btn btn-secondary mr-1" onClick={flipCurrentCard}>Flip</button>
              <button className="btn btn-primary disabled" onClick={handleNextCard}>Next</button>
            </div>
          ) : (
            <div className="card-body text-center">
              <h4 className="mt-4 mb-5 display-5">{cardData.back}</h4>
              <button className="btn btn-secondary mr-1" onClick={flipCurrentCard}>Flip</button>
              <button className="btn btn-primary" onClick={handleNextCard}>Next</button>
            </div>
          )}
        </div>
      )}
    </>
  );
}

export default Study;
