import React, { useEffect, useState, Fragment } from "react";
import DeckListItem from "../Components/DeckListItem";
import { listDecks, listCards, deleteDeck } from "../utils/api";

function DeckList() {
    const [decks, setDecks] = useState([]);

    useEffect(() => {
      const abortController = new AbortController();
      const signal = abortController.signal;

      async function getDecks() {
        try {
          const response = await listDecks(signal);
          setDecks(response);
        } catch (error) {
          console.log(error);
        }
      }
      getDecks();

      return () => abortController.abort();
      }, []);

      const handleDeleteDeck = (id) => {
        if (window.confirm("Are you sure you want to delete this deck?")) {
          deleteDeck(id);
          window.location.reload(true);
        } 
      }

    if (decks.length > 0) {
        return (
            <Fragment>
                <a href={`/decks/new`} className="btn btn-secondary mb-2"><span className="oi oi-plus"></span> Create Deck</a>
                <div className="list-group">
                    {decks.map((deck) => <DeckListItem key={deck.id} deck={deck} delete={handleDeleteDeck} />)}
                </div>
            </Fragment>
        );
    } else {
        return null;
    }
}

export default DeckList;
