import React, { useState } from "react";

function DeckList({ decks }) {
    const [deck, setDeck] = useState({});

    const handleDeckSelect = (id) => {
      const match = decks.find((deck) => deck.id === id);
      setDeck(match);
    }

    return decks.map((deck) => (
        <div key={deck.id} className="card mb-2">
            <div className="card-body">
                <h2>{deck.name}</h2>
                <p>{deck.description}</p>
                <a href={`/decks/${deck.id}`} className="btn btn-secondary mr-1" onClick={handleDeckSelect(deck.id)}>View</a>
                <a href={`/decks/${deck.id}/study`} className="btn btn-primary">Study</a>
            </div>
        </div>
    ));
}

export default DeckList;
