import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { listCards, readCard } from "../utils/api"

function Study({ name }) {
  const initialState = {
    isFlipped: false,
    front: "",
    back: "",
    progress: 0
  }

  const { deckId } = useParams();

  const [cards, setCards] = useState([]);
  const [card, setCard] = useState({ ...initialState });

  useEffect(() => {
    async function getCards() {
      try {
        const response = await listCards(deckId);
        setCards(response);
      } catch (error) {
          console.log(error);
      }
    }
    getCards();

  }, [deckId]);

  const handleNextCard = () => {
    const nextCard = cards.find((card, index) => index === card.progress)
    setCard({
      ...card,
      front: nextCard.front,
      back: nextCard.back,
      progress: card.progress++
    });
  }

  const flipCurrentCard = () => {
    setCard({
      ...card,
      isFlipped: !card.isFlipped
    });
    console.log(card.isFlipped)
  }

  return (
    <>
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
            <li className="breadcrumb-item"><a href="/"><span className="oi oi-home"></span> Home</a></li>
            <li className="breadcrumb-item"><a href={`/decks/${deckId}`}>{name}</a></li>
            <li className="breadcrumb-item text-secondary">Study</li>
        </ol>
      </nav>
      <h2>Study: {name}</h2>
      <div className="card mb-2">
        {card.isFlipped ? (
          <div className="card-body">
            <p>{card.front}</p>
            <button className="btn btn-secondary mr-1" onClick={flipCurrentCard}>Flip</button>
          </div>
        ) : (
          <div className="card-body">
            <p>{card.back}</p>
            <button className="btn btn-secondary mr-1" onClick={flipCurrentCard}>Flip</button>
            <button className="btn btn-primary" onClick={handleNextCard}>Next</button>
          </div>
        )}
      </div>
    </>
  );
}

export default Study;
