import React from "react";
import { useHistory, useParams } from "react-router-dom";
import { updateCard } from "../utils/api";

function CreateCard() {
    const history = useHistory();
    const { deckId, cardId } = useParams();

    const handleCardUpdate = () => {
        const formData = new FormData(document.querySelector("form"));
        const updated = {
            front: formData.get("front"),
            back: formData.get("back"),
            id: cardId,
            deckId: deckId
        }
        updateCard(updated);
        history.push(`/decks/${deckId}`)
        window.location.reload(true);
    }

    return (
        <>
            <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                    <li className="breadcrumb-item"><a href="/"><span className="oi oi-home"></span> Home</a></li>
                    <li className="breadcrumb-item text-secondary">Edit Card</li>
                </ol>
            </nav>
            <h2>Edit Card</h2>
            <form onSubmit={handleCardUpdate}>
                <div className="form-group">
                    <label htmlFor="card-front">Question</label>
                    <input type="text" className="form-control" id="card-front" name="front" placeholder="Question" />
                </div>
                <div className="form-group">
                    <label htmlFor="card-back">Answer</label>
                    <input type="text" className="form-control" id="card-back" name="back" placeholder="Answer" />
                </div>
                <a href={`/decks/${deckId}`} className="btn btn-secondary mr-1">Cancel</a>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </>
    );
}

export default CreateCard;
