import React, { useEffect, useState } from "react";
import { Switch, Route, useParams, useRouteMatch } from "react-router-dom";
import CardList from "../Components/CardList";
import Study from "../Components/Study";
import EditDeck from "../Components/EditDeck";
import CreateCard from "../Components/CreateCard";
import EditCard from "../Components/EditCard";
import { readDeck, listCards } from "../utils/api"

function Deck() {
  const { deckId } = useParams();
  const { url } = useRouteMatch();

  const [deck, setDeck] = useState({});
  const [cards, setCards] = useState([]);

  useEffect(() => {
    async function getDeck() {
      try {
        const response = await readDeck(deckId);
        setDeck(response);
      } catch (error) {
          console.log(error);
      }
    }
    getDeck();
  }, [deckId]);

  useEffect(() => {
      async function getCards() {
        try {
          const response = await listCards(deckId);
          setCards(response);
        } catch (error) {
            console.log(error);
        }
      }
      getCards();
    }, [deckId]);

  return (
    <Switch>
      <Route exact={true} path={`${url}`}>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item"><a href="/"><span className="oi oi-home"></span> Home</a></li>
            <li className="breadcrumb-item"><a href={`/decks/${deckId}`}>{deck.name}</a></li>
          </ol>
        </nav>
        <div className="card mb-4">
            <div className="card-body">
                <div className="d-flex justify-content-between">
                    <h2>{deck.name}</h2>
                </div>
                <p>{deck.description}</p>
                <a href={`/decks/${deckId}/study`} className="btn btn-primary mr-1"><span className="oi oi-book"></span> Study <span className="badge badge-light">{cards.length}</span></a>
                <a href={`/decks/${deckId}/edit`} className="btn btn-secondary mr-1"><span className="oi oi-cog"></span> Edit</a>
                <a href={`/decks/${deckId}/cards/new`} className="btn btn-secondary"><span className="oi oi-plus"></span> Add Cards</a>
                <a href={`/`} className="btn btn-danger float-right"><span className="oi oi-trash"></span></a>
            </div>
        </div>
        <h3>Cards</h3>
        <CardList cards={cards} />
      </Route>
      <Route path={`/decks/:deckId/study`}>
        <Study name={deck.name} />
      </Route>
      <Route path={`/decks/:deckId/edit`}>
        <EditDeck />
      </Route>
      <Route path={`/decks/:deckId/cards/new`}>
        <CreateCard />
      </Route>
      <Route path={`/decks/:deckId/cards/:cardId/edit`}>
        <EditCard />
      </Route>
    </Switch>
  );
}

export default Deck;
